document.addEventListener("DOMContentLoaded",function(){var a=document.getElementById("theme"),b=document.getElementById("mode");chrome.storage.sync.get({theme:"default",mode:"htmlmixed"},function(c){a.value=c.theme,b.value=c.mode});for(var c=document.querySelectorAll("select"),d=c.length-1;d>=0;d--)c[d].addEventListener("change",function(){var a=document.getElementById("theme"),b=document.getElementById("mode");chrome.storage.sync.set({theme:a.value,mode:b.value},function(){var a=document.getElementById("status");a.textContent="Options saved.",setTimeout(function(){a.textContent=""},1e3)})})});